import React, { Component, useState, useRef } from "react";
import {
  AppBar,
  Box,
  Toolbar,
  Typography,
  IconButton,
  InputAdornment,
  InputBase,
  Stack,
  TextField,
  Button,
} from "@mui/material/";
import "./Search.css";

import MenuIcon from "@mui/icons-material/Menu";
import { CenterFocusStrong } from "@mui/icons-material";

class Search extends Component {
  state = {};

  render() {
    const [movieName, setMovieName] = useState("");
    const getMovieName = () => {};

    const handleSeacrhInputChanges = () => {};
    return (
      <Stack
        spacing={1}
        direction="row"
        sx={{ height: "50px", mt: "20px", justifyContent: "center" }}
      >
        {/* <InputBase
          sx={{ ml: 1, flex: 1 }}
          placeholder="Search the movie"
          inputProps={{ "arial-label": "search google maps" }}
        ></InputBase> */}
        <TextField
          id="standard-basic"
          label="Movie"
          placeholder="Search the movie"
          onChange={handleSeacrhInputChanges}
          variant="standard"
          value={movieName}
          sx={{ width: "20rem" }}
        />
        <Button
          type="submit"
          variant="contained"
          onClick={getMovieName}
          value="SEARCH"
        >
          Search
        </Button>
      </Stack>
    );
  }
}

export default Search;
