import React, { Component } from "react";
import { useState } from "react";
import MovieLogo from "./movie-svgrepo-com.svg";
import "./App.css";
import { AppBar, Box, Toolbar, Typography, InputBase } from "@mui/material/";
import { styled, alpha } from "@mui/material/styles";
import SearchIcon from "@mui/icons-material/Search";
import axios from "axios";
import MovieComponent from "./components/MovieComponent";
import { PlayCircle } from "@mui/icons-material";

const API_KEY = "5b5644a0";

const Search = styled("div")(({ theme }) => ({
  position: "relative",
  borderRadius: theme.shape.borderRadius,
  backgroundColor: alpha(theme.palette.common.white, 0.15),
  "&:hover": {
    backgroundColor: alpha(theme.palette.common.white, 0.25),
  },
  marginLeft: 0,
  width: "100%",
  [theme.breakpoints.up("sm")]: {
    marginLeft: theme.spacing(1),
    width: "auto",
  },
}));

const SearchIconWrapper = styled("div")(({ theme }) => ({
  padding: theme.spacing(0, 2),
  height: "100%",
  position: "absolute",
  pointerEvents: "none",
  display: "flex",
  alignItems: "center",
  justifyContent: "center",
}));

const StyledInputBase = styled(InputBase)(({ theme }) => ({
  color: "inherit",
  "& .MuiInputBase-input": {
    padding: theme.spacing(1, 1, 1, 0),
    // vertical padding + font size from searchIcon
    paddingLeft: `calc(1em + ${theme.spacing(4)})`,
    transition: theme.transitions.create("width"),
    width: "100%",
    [theme.breakpoints.up("sm")]: {
      width: "40ch",
      "&:focus": {
        width: "50ch",
      },
    },
  },
}));

const imageStyle = {
  width: "48px",
  height: "48px",
};

const App = () => {
  const [searchQuery, updateSearchQuery] = useState("");
  const [timeoutId, updateTimeoutId] = useState("");
  const [movieList, updateMovieList] = useState("");

  const fetchData = async (searchString) => {
    const response = await axios.get(
      `https://www.omdbapi.com/?s=${searchString}}&apikey=${API_KEY}`
    );
    updateMovieList(response.data.Search);
  };

  const onTextChange = (e) => {
    clearTimeout(timeoutId);
    updateSearchQuery(e.target.value);
    const timeout = setTimeout(() => {
      fetchData(e.target.value);
    }, 500);
    updateTimeoutId(timeout);
  };

  return (
    <React.Fragment>
      <Box sx={{ flexGrow: 1 }}>
        <AppBar
          position="static"
          sx={{
            backgroundColor: "black",
          }}
        >
          <Toolbar>
            <div style={imageStyle}>
              <img src={MovieLogo} alt="React Logo" />
            </div>
            <Typography
              variant="h6"
              noWrap
              component="div"
              sx={{ flexGrow: 1, display: { xs: "none", sm: "block" } }}
            >
              React Movie App
            </Typography>
            <Search>
              <SearchIconWrapper>
                <SearchIcon />
              </SearchIconWrapper>
              <StyledInputBase
                placeholder="Searchâ€¦"
                inputProps={{ "aria-label": "search" }}
                value={searchQuery}
                onChange={onTextChange}
              />
            </Search>
          </Toolbar>
        </AppBar>
      </Box>
      {movieList?.length ? (
        <MovieComponent movieData={movieList}></MovieComponent>
      ) : (
        <placeholder src={MovieLogo}></placeholder>
        // <Typography
        //   variant="h6"
        //   noWrap
        //   component="div"
        //   sx={{ flexGrow: 1, padding: "2em 28em 1em" }}
        // >
        //   No Movie Search
        // </Typography>
      )}
    </React.Fragment>
  );
};

export default App;
