import * as React from "react";
import Card from "@mui/material/Card";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
import { fontWeight } from "@mui/system";

const movieListContainer = {
  display: "flex",
  flexDirection: "row",
  flexWrap: "wrap",
  padding: "30px",
  justifyContent: "space-evenly",
};

const MovieContainer = {
  display: "flex",
  flexDirection: "column",
  padding: "10px",
  width: "280px",
  boxShadow: "0 3px 10px 0 #aaa",
  cursor: "pointer",
};

const InfoColumn = {
  fontSize: "16px",
  fontWeight: "500",
  color: "black",
  textTransform: "capitalize",
};

const MovieComponent = (props) => {
  return (
    <div style={movieListContainer}>
      <Card sx={{ maxWidth: "280px" }}>
        <CardMedia
          component="img"
          height="362px"
          image="https://m.media-amazon.com/images/M/MV5BNDYxNjQyMjAtNTdiOS00NGYwLWFmNTAtNThmYjU5ZGI2YTI1XkEyXkFqcGdeQXVyMTMxODk2OTU@._V1_SX300.jpg"
          alt="green iguana"
        />
        <CardContent>
          <Typography
            component="span"
            sx={{
              fontSize: "18px",
              fontWeight: "600",
              color: "black",
              margin: "15px 0",
              whiteSpace: "nowrap",
              textOverflow: "ellipsis",
              overflow: "hidden",
            }}
          >
            The Avengers
          </Typography>
        </CardContent>
        <CardActions
          sx={{ flexDirection: "row", justifyContent: "space-between" }}
        >
          <span style={InfoColumn}>Year:2012</span>
          <span style={InfoColumn}>Type:Movie</span>
        </CardActions>
      </Card>
    </div>
  );
};

export default MovieComponent;
