import * as React from "react";
import Card from "@mui/material/Card";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
import { fontWeight } from "@mui/system";
import Box from "@mui/material/Box";
import { convertLength } from "@mui/material/styles/cssUtils";

const MovieInfoComponent = (movieSeLect) => {
  console.log(movieSeLect);

  const ImageContainer = {
    objectFit: "conver",
    height: "352px",
  };

  const movieInfo = {
    fontSize: "16px",
    fontWeight: "500",
    color: "black",
    margin: "4px 0",
    overflow: "hidden",
    textTransform: "capitalize",
    textOverflow: "ellipsis",
  };
  return (
    <Box
      component="div"
      sx={{
        display: "flex",
        flexDirection: "row",
        p: "20px 30px",
        justifyContent: "center",
        borderBottom: "1px solid lightgray",
      }}
    >
      <img
        style={ImageContainer}
        src="https://m.media-amazon.com/images/M/MV5BMjRjMTYwMTYtMmRkNi00MmVkLWE0MjQtNmM3YjI0NWFhZDNmXkEyXkFqcGdeQXVyODE5NzE3OTE@._V1_SX300.jpg"
      ></img>
      <Box sx={{ display: "flex", flexDirection: "column", m: "20px" }}>
        <Typography
          component="span"
          sx={{
            fontSize: "18px",
            fontWeight: "600",
            color: "black",
            margin: "15px 0",
            whiteSpace: "nowrap",
            textOverflow: "ellipsis",
            overflow: "hidden",
          }}
        >
          Movie: Iron Man
        </Typography>
        <Typography component="span" style={movieInfo}></Typography>
        <Typography component="span" style={movieInfo}>
          IMDB Rating : 9.0
        </Typography>
        <Typography component="span" style={movieInfo}>
          IMDB Rating : 9.0
        </Typography>
        <Typography component="span" style={movieInfo}>
          IMDB Rating : <span sx={{ opacity: "0" }}>9.0</span>
        </Typography>
        <Typography component="span" style={movieInfo}>
          IMDB Rating : 9.0
        </Typography>
      </Box>
    </Box>
  );
};
export default MovieInfoComponent;
